import React from 'react';
import { Button, Table, Image, Tag, Modal, message } from 'antd';
import PropTypes from 'prop-types';
import { EditOutlined, DeleteOutlined, ExclamationCircleOutlined } from '@ant-design/icons';
import useProductList from '../hooks/useProductList';
import { productCategoryList } from '../../../constants';
import { getCategoryName } from '../../../utils';
import productAPI from '../../../api/productAPI';

const { confirm } = Modal;


const columns = [
  {
    title: 'Tên sản phẩm',
    width: 150,
    dataIndex: 'name',
    key: 'name',
    fixed: 'left',
  },
  {
    title: 'Hình ảnh',
    width: 200,
    dataIndex: 'imageSrc',
    key: 'imageSrc',
    fixed: 'left',
    render: (imageSrc) => (
      <Image
        width={200}
        height={200}
        src={imageSrc}
        fallback="data:image/png;base64,"
      />
    ),
  },
  {
    title: 'Loại sản phẩm',
    width: 100,
    dataIndex: 'category',
    key: 'category',
    render: (category) => getCategoryName(category),
  },
  {
    title: 'Nội dung',
    dataIndex: 'content',
    key: 'content',
    width: 200,
  },
  {
    title: 'Link Shoppe',
    dataIndex: 'shoppeLink',
    key: 'shoppeLink',
    width: 100,
    render: (shoppeLink) =>
      shoppeLink && (
        <a href={shoppeLink} target="_blank">
          <Button type="primary">Link Shoppe</Button>
        </a>
      ),
  },
  {
    title: 'Link Facebook',
    dataIndex: 'facebookLink',
    key: 'facebookLink',
    width: 100,
    render: (facebookLink) =>
      facebookLink && (
        <a href={facebookLink} target="_blank">
          <Button type="primary">Link Facebook</Button>
        </a>
      ),
  },
  {
    title: 'Kích hoạt',
    dataIndex: 'isActive',
    key: 'isActive',
    render: (isActive) => (isActive ? <Tag color="#87d068">Đã kích hoạt</Tag> : <Tag color="#f50">Chưa kích hoạt</Tag>),

    width: 80,
  },
  {
    title: 'Hành động',
    key: 'operation',
    fixed: 'right',
    width: 200,
    render: (_, record) => (
      <div className="action-container">
        <Button type="primary" icon={<EditOutlined />} style={{ marginRight: 6 }}>
          Chỉnh sửa
        </Button>
        <Button type="primary" danger icon={<DeleteOutlined />} onClick={() => showConfirm(record._id)}>
          Xóa
        </Button>
      </div>
    ),
  },
];

function showConfirm(id) {
  confirm({
    title: 'Muốn xóa sản phẩm này thật à?',
    closable: true,
    icon: <ExclamationCircleOutlined />,
    content: 'Xóa là mất luôn đấy nhá',
    okText: "Ừ, xóa :)",
    cancelText: "Không xóa nữa",
    onOk: async () => {
      try {
        const product = await productAPI.delete(id)
        (() => {
          message.success('Xóa thành công rồi nha');
        })()
      } catch (error) {
        (() => {
          message.error('Xóa không thành công...');
        })()
      }
    },
    onCancel() {
      console.log('Cancel');
    },
  });
}

function ListPage(props) {
  const { productList, loading, pagination } = useProductList();
  return (
    <div style={{ height: '100%' }}>
      <Table columns={columns} dataSource={productList} scroll={{ x: 1500, y: '65vh' }} />,
    </div>
  );
}

ListPage.propTypes = {};

export default ListPage;
